import { Component } from 'react';
import './StyleHeader.css';

class Header extends Component
{
    state = {
        allLetters: ["APOD", "DONKI", "EPIC" , "Techport"],
        currentLetters: [],
      };
      componentDidMount() {
        setInterval(this.timer, 1000);
      }
      timer = () => {
        this.setState((state, _) => {
          const currentLettersLength = state.currentLetters.length;
          let currentLetters = [];
          if (currentLettersLength !== 3)
            currentLetters = [
              ...state.currentLetters,
              state.allLetters[currentLettersLength],
            ];
          return {
            currentLetters: currentLetters,
          };
        });
      };
      render() {
        return (
            <div >
            <p className="header">NASA APIs</p>
            <p>Here: {this.state.currentLetters.map((letter) => `${letter} `)}</p>;
        </div>
        )
      }
    }

export default Header;
